import React from 'react'
import Game from '../Components/Game'
import { Redirect, withRouter, Route } from 'react-router-dom'

class GameContainer extends React.Component {

    state = {
        user: this.props.user,
        numberOfQuestions: '',
        category: '',
        difficulty: '',
    }

    componentDidMount() {
        console.log(this.props)
    }

    changeHandler = (e) => {
        this.setState({ [e.target.name]: [e.target.value]})
    }

    createNewGame = () => {
        if ((this.state.numberOfQuestions !== '') && (this.state.category !== '') && (this.state.difficulty !== '')) {
            <Game category={this.state.category} numberOfQuestions={this.state.numberOfQuestions} diffculty={this.state.difficulty} />
        }
    }


    render() {
        return (
        <>
            {/* {this.props.user ? */}
                <div>
                    <h1>You're here!</h1>
                    <h2>Set your Game Settings</h2>
                        <form onSubmit={this.createNewGame}>
                            Difficulty:
                                <select name="difficulty" value={this.state.diffculty} onChange={this.changeHandler}>
                                    <option value=""></option>
                                    <option value="easy">Easy</option>
                                    <option value="medium">Medium</option>
                                    <option value="difficult">Difficult</option>
                                </select>
                            Category:
                                <select name="category" value={this.state.category} onChange={this.changeHandler}>
                                    <option value=""></option>
                                    <option value="Entertainment: Film">Film</option>
                                    <option value="Geography">Geography</option>
                                    <option value="Entertainment: Music">Music</option>
                                    <option value="Science: Computers">Computer Science</option>
                                    <option value="Sports">Sports</option>
                                    <option value="History">History</option>
                                    <option value="Mythology">Mythology</option>
                                    <option value="Science & Nature">Science & Nature</option>
                                    <option value="Politics">Politics</option>
                                    <option value="Entertainment: Video Games">Video Games</option>
                            </select>
                            Number of Questions:
                            <select name="" value={this.state.numberOfQuestions} onChange={this.changeHandler}>
                                <option value="5">5</option>
                                <option value="10">10</option>
                                <option value="15">15</option>
                            </select>
                        </form>
                </div>
                {/* :
                <Redirect to="/login" /> */}

        </>
            );
    }
}

export default withRouter(GameContainer)
