import React, { Component } from 'react';
import './App.css';
import Login from './Components/Login'
import Signup from './Components/Signup'
import { Redirect, Route, Router, Switch, withRouter } from 'react-router-dom'
import GameContainer from './Containers/GameContainer'
import { createBrowserHistory } from 'history';

const BASE_API = 'http://localhost:3000'



const history = createBrowserHistory();


class App extends Component {

  state = {
    user: null
  }

  componentDidMount() {
    console.log(this.state.user)
    const token = localStorage.getItem("token")

    if (token) {
      fetch(`${BASE_API}/profile`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(r => r.json())
        .then(data => {
        console.log(data)
        this.setState({ user: data.user }); history.push('/lobby');
      })
      .catch(error => console.log(error))
    } else {
      history.push('/signup')
    }
  }

  signupHandler = (userObj) => {
    fetch(`${BASE_API}/users`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userObj})
    })
    .then(r => r.json())
    .then(data => {
        this.setState({ user: data })
        localStorage.setItem("token", data.jwt)
    })
    .catch(error => console.log(error))
  }

  loginHandler = (userInfo) => {
    console.log(userInfo)
    fetch(`${BASE_API}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userInfo})
    })
    .then(r => r.json())
    .then(data => {
        this.setState({ user: data})
        localStorage.setItem("token", data.jwt)
    })
    .catch(error => console.log(error))
  }



  render() {
    return (
        <div>
        <Switch history={this.props.history}>
          <Route exact path="/login" render={() => <Signup submitHandler={this.loginHandler} />} />
          <Route exact path="/lobby" render={() => <GameContainer user={this.state.user}/> } />
          <Route exact path="/signup" render={() => <Signup submitHandler={this.signupHandler} />} />
        </Switch>
        {this.state.user ? <button onClick={() => localStorage.removeItem('token')}>Logout</button> : null}
        </div>
      );
    }

  }


export default App;
