import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Login from './Components/Login'
import Signup from './Components/Signup'
import { Route, Switch, Redirect } from 'react-router-dom'
import GameContainer from './Containers/GameContainer'

const BASE_API = 'http://localhost:3000'

class App extends Component {

  state = {
    user: null
  }

  componentDidMount(){
    const token = localStorage.getItem("token")
    if (token) {
      fetch(`${BASE_API}/profile`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}`},
      })  
      .then(r => r.json())
      .then(data => this.setState( {user: data.user} ))
    }
  }

  signupHandler = (userObj) => {
    fetch(`${BASE_API}/users`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userObj})
    })
    .then(r => r.json())
      .then(data => {
        localStorage.setItem("Token", data.jwt)
        this.pushState('/lobby')
        window.history.forward('lobby')
    })
  }

  loginHandler = (userInfo) => {
    console.log(userInfo)
    fetch(`${BASE_API}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userInfo})
    })
    .then(r => r.json())
      .then(data => {
        this.pushState('/lobby')
        window.history.forward('lobby')
        localStorage.setItem(data.jwt)
    })
  }



  render(){


  return (
    <Switch>
      <Route path="/login" render={() => <Signup submitHandler={this.loginHandler}/>}/>
      <Route path="/signup" render={() => <Signup submitHandler={this.signupHandler} />} />
      <Route path="/lobby" render={() => this.state.user ? <GameContainer user={this.state.user}/> : <Redirect to='/signup'/>} />
    </Switch>
  );

  }
}

export default App;
