import React from 'react'

class Answer extends React.Component {

    state = {
        htmlID: ''
    }


    changeColor = () => {

        switch (this.props.value) {
            case 0:
                this.setState({ htmlID: "clicked-wrong" })
                break;
            case 1:
                this.setState({ htmlID: "clicked-right" })
                break;
            default:
                console.log('check the switch')
        }
        this.props.nextQuestion(this.props.value)
    }


    render() {
        return (
            <div className="answer" id={this.state.htmlID} onClick={() => this.changeColor()}>
            {this.props.answer}
        </div>
        )
    }
}

export default Answer
