import React, { Component } from 'react';
import './App.css';
import Login from './Components/Login'
import Signup from './Components/Signup'
import { Route, Switch, Redirect, createBrowserHistory } from 'react-router-dom'
import GameContainer from './Containers/GameContainer'

const BASE_API = 'http://localhost:3000'

const history = createBrowserHistory();

class App extends Component {

  // static propTypes = {
  //   match: PropTypes.object.isRequired,
  //   location: PropTypes.object.isRequired,
  //   history: PropTypes.object.isRequired
  // };

  state = {
    user: null
  }



  componentDidMount(){
    const token = localStorage.getItem("token")
    if (token) {
      fetch(`${BASE_API}/profile`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}`},
      })  
      .then(r => r.json())
      .then(data => this.setState( {user: data.user} ))
    } else {
      // createBrowserHistory.push('/login')
    }
  }

  signupHandler = (userObj) => {
    fetch(`${BASE_API}/users`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userObj})
    })
    .then(r => r.json())
    .then(data => {
      localStorage.setItem("Token", data.jwt);
      console.log(this)
      this.props.history.push('/lobby')
    })
  }

  loginHandler = (userInfo) => {
    console.log(userInfo)
    fetch(`${BASE_API}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userInfo})
    })
    .then(r => r.json())
      .then(data => {
        localStorage.setItem("Token", data.jwt)
    })
  }



  render() {

    if (this.state.user === undefined) {
      return (
        <div><h1>Loading...</h1></div>
      )
    }


  return (
    <Switch>
      <Route path="/login" render={() => <Signup component={Login} submitHandler={this.loginHandler}/>}/>
      <Route path="/signup" render={() => <Signup omponent={Signup} submitHandler={this.signupHandler} />} />
      <Route path="/lobby" render={() => <GameContainer component={GameContainer}  user={this.state.user}/>} />
    </Switch>
  );

  }
}

export default App;
