import React from "react"
import { withRouter } from 'react-router-dom'
import Question from './Question'

const BASE_API = 'http://localhost:3000'

class Game extends React.Component {

    state = {
        allQuestions: [],
        filteredQuestions: [],
        category: this.props.category,
        numberOfQuestions: this.props.numberOfQuestions,
        difficulty: this.props.difficulty
    }

    componentDidMount() {

        fetch(`${BASE_API}/questions`)
            .then(resp => resp.json())
            .then(data => this.setState({ allQuestions: data }))
            .then(() => this.getQuestions() )
    }

    shuffle = (a) =>  {
    var j, x, i;
    for (i = a.length - 1; i > 0; i--) {
        j = Math.floor(Math.random() * (i + 1));
        x = a[i];
        a[i] = a[j];
        a[j] = x;
    }
    return a;
}

    getQuestions = () => {
        let filteredArray = this.state.allQuestions.filter(question => {
            if ((question.category === this.state.category) && (question.difficulty === this.state.difficulty)) {
                return question
            }
        })
        let shuffledArray = this.shuffle(filteredArray)
        let slicedArray = shuffledArray.slice(0, 10)
        this.setState({ filteredQuestions: slicedArray })
    }

    nextQuestion = () => {

    }

    renderQuestion = () => {
        let singleQuestion = this.state.filteredQuestions.slice(0, 1);
        return <Question key={singleQuestion.id} question={singleQuestion} shuffle={this.shuffle} />
    }



    render() {
        return (
            <div>
                <h1>{this.state.category}</h1>
                <h1>{this.state.difficulty}</h1>
                <h1>{this.state.numberOfQuestions}</h1>
                {this.renderQuestion()}
            </div>
        )
    }

}


export default withRouter(Game)
