import React from 'react'
import Answer from './Answer'

class Question extends React.Component {

    state = {

    }
    componentDidUpdate() {
        console.log(this.props)

    }

    renderAnswers = () => {
        if (this.props.question) {
            let wrongAnswers = this.props.question.incorrect_answers.map((answer, index) => {
                return <Answer id="incorrect" key={index} value={0} answer={answer} nextQuestion={this.props.nextQuestion}/>
            })
            let rightAnswer = <Answer id="correct" key={this.props.question.correct_answer} value={1} answer={this.props.question.correct_answer} />
            let answers = [...wrongAnswers, rightAnswer]

            return this.props.shuffle(answers)
        }
    }

    render() {
        return (
            <>
                {this.props.question ?
                    <div className="question">
                        <h1 className="question-title">Question: {this.props.question.question}</h1>
                        {this.renderAnswers()}
                    </div>
                    :
                    <h1>No questions yet</h1>
                }
            </>
        )

    }

}

export default Question
