import React from 'react'

class Answer extends React.Component {

    state = {
        htmlID: '',
        clicked: false,
    }


    submitAnswer = () => {

        switch (this.props.value) {
            case 0:
                this.setState({ htmlID: "clicked-wrong" })
                break;
            case 1:
                this.setState({ htmlID: "clicked-right" })
                break;
            default:
                console.log('Check the Answer Switch')
        }
        this.props.nextQuestion(this.props.value)
    }


    render() {
        return (
            <button className="answer" id={this.state.htmlID} onClick={() => { this.state.clicked ? null : this.submitAnswer() }}>
                { this.props.answer }
            </button >
        )
    }
}

export default Answer
