import React from 'react'

class Answer extends React.Component {


    changeColor = () => {

        this.props.nextQuestion(this.props.value)
    }


    render() {
        return (
        <div className="answer" onClick={() => this.changeColor()}>
            {this.props.answer}
        </div>
        )
    }
}

export default Answer
