import React, { Component } from 'react';
import './App.css';
import Login from './Components/Login'
import Signup from './Components/Signup'
import { Route, Router, Switch, withRouter } from 'react-router-dom'
import GameContainer from './Containers/GameContainer'
import { createBrowserHistory } from 'history';

const BASE_API = 'http://localhost:3000'



const history = createBrowserHistory();


class App extends Component {

  state = {
    user: null
  }

  componentDidMount(){
    const token = localStorage.getItem("token")
    if (token) {
      fetch(`${BASE_API}/profile`, {
        method: "GET",
        headers: { Authorization: `Bearer ${token}`},
      })  
      .then(r => r.json())
      .then(data => this.setState( {user: data.user} ))
    } else {
      this.props.history.push('/signup')
    }
  }

  signupHandler = (userObj) => {
    fetch(`${BASE_API}/users`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userObj})
    })
    .then(r => r.json())
    .then(data => {
      localStorage.setItem("Token", data.jwt);
      this.props.history.push('/lobby')
    })
  }

  loginHandler = (userInfo) => {
    console.log(userInfo)
    fetch(`${BASE_API}/login`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json'
      },
      body: JSON.stringify({user: userInfo})
    })
    .then(r => r.json())
      .then(data => {
        localStorage.setItem("Token", data.jwt)
        this.props.history.push('/lobby')
    })
  }



  render() {

    if (this.state.user === undefined) {
      return (
        <div><h1>Loading...</h1></div>
      )
    }


    return (
        <Switch history={this.props.history}>
          <Route exact path="/login" render={() => <Signup component={Login} submitHandler={this.loginHandler}/>}/>
          <Route exact path="/lobby" render={() => <GameContainer user={this.state.user} />} />
          <Route exact path="/signup" render={() => <Signup component={Signup} submitHandler={this.signupHandler} />} />
        </Switch>
  );

  }
}

export default App;
