import React from 'react'
import Answer from './Answer'

class Question extends React.Component {

    state = {

    }

    renderAnswers = () => {
        if (this.props.question) {
            let answers = this.props.question.incorrect_answers.map(answer => {
                return <Answer id="incorrect" key={this.props.question.id} value={0} answer={answer} />
            })
            let correctAnswer = this.props.question.correct_answer.map(answer => {
                return <Answer id="correct" key={this.props.question.id} value={1} answer={answer} />
            })
            answers.push(correctAnswer[0])
            return this.props.shuffle(answers)
        }
    }

    render() {
        return (
            <div className="question">
                <h1>{this.props.question}</h1>
                    {this.renderAnswers()}
            </div>

        )

    }

}

export default Question
